import SearchBar from '@/components/SearchBar';
import ProfileImage from '@/components/ProfileImage';
import Subjects from '@/components/Subjects';
import Filters from '@/components/Filters';

export default {
	name : 'top-bar',

	props : {
		state : {
			required : true,
			type     : String
		},
		scrollTop : {
			required : true,
		}
	},

	data : () => ( {
		subjectState : 'closed',

		searchTimeout : null,
		query         : '',

	} ),

	computed : {
		user() {
			return this.$store.state.user;
		},

		selectedFilters() {
			return this.$store.state.selectedFilters;
		},

		model() {
			return this.$store.state.model;
		},

		activePrimary() {
			const { model } = this;

			if ( !Object.keys( model ).length ) {
				return null;
			}

			return this.selectedFilters[model.primaryFilter];
		},

		opacity() {
			const num = Math.max( 115 - this.scrollTop, 0 );
			const den = 115;

			return ( num / den );
		},

		fromSectorInfo() {
			return this.$route.query.fromSectorInfo;
		}

	},

	methods : {

		search( query ) {
			if ( this.searchTimeout ) {
				window.clearTimeout( this.searchTimeout );
			}

			this.searchTimeout = window.setTimeout( () => {
				this.$store.dispatch( 'search', query );
			}, 400 );
		},

		toggleSearchState() {
			if ( this.state !== 'search' ) {
				this.$emit( 'update-topbar', 'search' );
				return;
			}

			const state = this.scrollTop > 50 ? 'collapsed' : '';
			this.$emit( 'update-topbar', state );
		},

		toggleSubjectBar() {
			if ( this.subjectState === 'expanded' ) {
				this.subjectState = 'closed';
				return;
			}

			this.subjectState = 'expanded';
		},

		toggleMenu() {
			this.$store.dispatch( 'toggleSideMenu' );
		},

		openUserSwiper() {
			this.$store.dispatch( 'updateStore', ['userSwiperState', 'open'] );
		},

		giveFeedback() {
			SpacebarFeedback.open();

		},
	},

	components : {
		SearchBar,
		ProfileImage,
		Subjects,
		Filters
	}

};
