#upload-errors {

	.swiper-slide.empty + .swiper-slide {
		background: rgba(255,255,255,0.8) !important;
		display: flex;
		align-items: center;
		justify-content: center;
	}

	.main {
		background: white;
		border-radius: 10px;
		box-shadow: $floatingObject;
		overflow: hidden;
		width: 80%;
		max-width: 800px;
		opacity: 0;
		transition: opacity 0.2s ease;
		position: relative;
		height: 500px;
		display: flex;
		flex-direction: column;

		&.open {
			opacity: 1;
		}

		.loading-indicator {
			background: white;
			border-radius: 10px;

			.loading-circles {
				position: absolute;
				top: 50%;
				left: 50%;
				transform: translate(-50%,-50%);
			}
		}

		.header {
			display: flex;
			align-items: center;
			justify-content: space-between;
			padding: 25px;

			h1 {
				color : $primary;
			}
		}

		.body {
			display: none;
			flex-direction: column;
			flex: 1 1 0;
			opacity: 0;
			pointer-events: none;
			transition: all .2s ease;
			overflow: auto;
			padding: 0px 25px 25px;

			&.active {
				display: flex;
				opacity: 1;
				pointer-events: all;
			}

			.big {
				display: flex;
				flex-direction: column;
				align-items: center;
				justify-content: center;
				flex: 1 0 0;

				&.green {
					color: $confirm;
				}

				&.red {
					color: $cancel;
				}

				h1 {
					font-size: 40px;
					margin-bottom: 15px;
					color: $primary;
				}

				i.material-icons {
					font-size: 100px;
				}

			}


			.errors {
				margin-top: 25px;

				.error {
					display: flex;
					align-items: center;
					margin: 10px 0px;
					padding: 10px;
					border-bottom: 1px solid $secondary;

					&.warning {
						.icon {
							i.material-icons {
								color: $warning;
							}
						}
					}

					&.fatal {
						.icon {
							i.material-icons {
								color: $cancel;
							}
						}
					}

					p.title {
						font-weight: 600;
						font-size: 18px;
					}

					.icon {
						margin-right: 10px;

						i.material-icons {
							font-size: 30px;
						}

					}

				}
			}
		}

	}

}
